import { Field, reduxForm } from 'redux-form'
import { maxLengthCreator, required } from '../../../../utilits/validators'
import { Input } from '../../../common/FormsControl/FormsControl'
import s from '../ProfileInfo.module.css'

const maxLength30 = maxLengthCreator(30)

const BlaBla = (props) => {
    const onSubmit = (formData) => {
        console.log(formData.fullName)
    }
    return <FormDescription onSubmit={onSubmit} {...props} />
}

const DescriptionForm = (props) => {
    return (
        <form className={s.descriptionTextForm} onSubmit={props.handleSubmit}>
            <div className={s.main}>
                <div className={s.fullNameForm}>
                    Name:
                    <Field placeholder={props.fullName} name={'fullName'} component={Input} validate={[required, maxLength30]} />
                </div>
            </div>
             <div className={s.about}>About Me
                <div>{props.aboutMe}</div>
                <div>About Job: {props.lookingForAJob ? "Looking for a job: " : "Not looking for a job"} {props.lookingForAJobDescription}</div>
                <div>Contacts</div>
                <div className={s.contacts}>
                    {Object.keys(props.contacts).map(key => {
                        return <div>{key}:{props.contacts[key]}</div>
                    })}
                </div>
            </div> 
            <button className={s.formButton}>Save changes</button>
        </form>
    )
}


const FormDescription = reduxForm({
    form: 'profile'
})(DescriptionForm)

export default BlaBla
