import React from "react";
import { Form, reduxForm } from "redux-form";
import s from './../Dialogs.module.css';

const MessageInput = (props) => {
    let onAddMessage = () => {
        props.addMessage();
    };
    let onMessageChange = (e) => {
        let text = e.target.value; // замена ref ссылкам
        props.messageChange(text);
    };

    return (
        <div className={s.messageInput}>
            <Message />
            <textarea placeholder='Enter your message' onChange={onMessageChange} value={props.messageInput} />
            <button onClick={onAddMessage}>Send</button>
        </div>
    )
};

const MessageForm = () => {
    return (
        <Form>
            <Field placeholder='Enter your message' name={'message'} component={'input'} />
            <button onClick={onAddMessage}>Send</button>
        </Form>
    )
}

const Message = reduxForm({
    form: 'Message'
})(MessageForm)

export default MessageInput;
