import Preloader from '../../common/Preloader/Preloader';
import s from './ProfileInfo.module.css'
import ProfileStatusWithHooks from './ProfileStatusHook';
import img from '../../../assets/images/avaDefault.jpeg'

const ProfileInfo = (props) => {
  if (!props.profile) {
    return <Preloader />
  }

  const onNewPhoto = (e) => {
    if (e.target.files.length) {
      props.updatePhoto(e.target.files[0])
    }
  }

  return (
    <div className={s.descriptionBlock}>

      <img src={props.profile.photos.large || img} >
        {props.isOwner && <input type='file' onChange={onNewPhoto} />}
      </img>

      <div className={s.descriptionText}>



        <div className={s.main}>
          <div className={s.fullName}>{props.profile.fullName}</div>
          <ProfileStatusWithHooks status={props.status} updateStatus={props.updateStatus} />
        </div>

        <div className={s.about}>About Me
          <div>
            {props.profile.aboutMe}
          </div>
          <div>About Job: {props.profile.lookingForAJob ? "Looking for a job: " : "Not looking for a job"} {props.profile.lookingForAJobDescription}</div>
          <div>Contacts</div>
          <div className={s.contacts}>
            <div>facebook: {props.profile.contacts.facebook}</div>
            <div>website: {props.profile.contacts.website}</div>
            <div>vk: {props.profile.contacts.vk}</div>
            <div>instagram: {props.profile.contacts.instagram}</div>
            <div>youtube: {props.profile.contacts.youtube}</div>
            <div>github: {props.profile.contacts.github}</div>
            <div>mainLink: {props.profile.contacts.mainLink}</div>
          </div>
        </div>

      </div>

    </div>
  );
}

export default ProfileInfo;
