import React from "react";
import { Field, reduxForm } from "redux-form";
import { maxLengthCreator, required } from "../../../utilits/validators";
import { Textarea } from "../../common/FormsControl/FormsControl";
import s from './../Dialogs.module.css';

const maxLength10 = maxLengthCreator(10)

const MessageInput = (props) => {
    const onSubmit = (formData) => {
        props.addMessage(formData.message)
    }
    return (
        <div className={s.messageInput}>
            <input type='submit' form='profile' value='save' onClick={props.deactivateMode} />
            <Message onSubmit={onSubmit} />
        </div>
    )
}

const MessageForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field placeholder='Enter your message' name={'message'} component={Textarea} validate={[required, maxLength10]}/>
            </div>
            <div>
                <button>Send</button>
            </div>
        </form>
    )
}

const Message = reduxForm({
    form: 'Message'
})(MessageForm)

export default MessageInput;
