import s from '../ProfileInfo.module.css'
import { Field, Form, Formik } from 'formik'
import * as yup from 'yup'

const DescriptionForm = (props) => {
    const validationForm = yup.object().shape({
        name: yup.string().typeError('It must be string').required('This field is required')
    })

    return <div>
        <Formik
            initialValues={{
                fullName: props.fullName,
                aboutMe: props.aboutMe ? props.aboutMe : '',
                lookingForAJob: props.lookingForAJob ? props.lookingForAJob : '',
                lookingForAJobDescription: props.lookingForAJobDescription ? props.lookingForAJobDescription : '',
                contacts: {
                    github: '',
                    vk: '',
                    facebook: '',
                    instagram: '',
                    twitter: '',
                    website: '',
                    youtube: '',
                    mainLink: ''
                }
            }}
            validateOnBlur
            onSubmit={(values) => { props.updateProfile(values) }}
            validationSchema={validationForm}
        >
            {({ values, errors, touched, handleChange, handleBlur, isValid, handleSubmit, dirty }) => (
                <Form>
                    <div>
                        <label htmlFor="fullName">Name: </label>
                        <Field type={"text"}
                            name={"fullName"}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.fullName}
                            placeholder={props.fullName} />
                    </div>
                    <div>
                        <label htmlFor="lookingForAJob">lookingForAJob: </label>
                        <Field type="checkbox" name="lookingForAJob" />
                        {`${values.lookingForAJob}`}
                    </div>
                    <div>
                        <label htmlFor="lookingForAJobDescription">lookingForAJobDescription: </label>
                        <Field type={"text"}
                            name={"lookingForAJobDescription"}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.lookingForAJobDescription} />
                    </div>
                    <div>Contacts</div>
                    <div className={s.contacts}>
                        <div>
                            <label htmlFor="github">github: </label>
                            <Field type={"text"}
                                name={"github"}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                value={values.contacts.github} />
                        </div>
                    </div>
                    {touched.name && errors.name && <p>{errors.name}</p>}
                    <button disabled={!isValid || !dirty} type={'submit'} onClick={handleSubmit}>Save changes</button>
                </Form>
            )}
        </Formik>
    </div>
}

export default DescriptionForm;
