import s from './Dialogs.module.css';
import MessageForm from './MessageForm/MessageForm';
import Message from './MessageForm/MessagesElement';
import Dialog from './MessageForm/DialogsElement'

const Dialogs = (props) => {

    let messagesElements = props.messagesData
        .map(m => <div key={m.id} className={s.message}>{m.message}</div>)

    let dialogsElements = props.dialogsData
        .map(d => <Dialog id={d.id} name={d.name} img={d.img} />)

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogsElements} 
            </div>
            <div className={s.messages}>
                {messagesElements}
                <MessageForm addMessage={props.sendMessage} />
            </div>
        </div>
    );
}

export default Dialogs;
