import s from '../ProfileInfo.module.css'

const DescriptionForm = (props) => {
    return (
        <form className={s.descriptionText}>
            <div className={s.main}>
                <div className={s.fullName}>{props.fullName}</div>
                <ProfileStatusWithHooks status={props.status} updateStatus={props.updateStatus} />
            </div>
            <div className={s.about}>About Me
                <div>{props.aboutMe}</div>
                <div>About Job: {props.lookingForAJob ? "Looking for a job: " : "Not looking for a job"} {props.lookingForAJobDescription}</div>
                <div>Contacts</div>
                <div className={s.contacts}>
                    {Object.keys(props.contacts).map(key => {
                        return <div>{key}:{props.contacts[key]}</div>
                    })}
                </div>
            </div>
        </form>
    )
}
export default DescriptionForm;






const Login = ({ login, isAuth, userId }) => {
    const onSubmit = (formData) => {
        login(formData.email, formData.password, formData.rememberMe)
    }

    if (isAuth) {
        return <Navigate to={`/profile/${userId}`} />
    }
    return (
        <div>
            <h1>Login</h1>
            <LoginReduxForm onSubmit={onSubmit} />
        </div>
    )
}

const LoginForm = ({ handleSubmit, error }) => {
    return (
        <form onSubmit={handleSubmit}>
            <div>
                <Field placeholder="Email" name={'email'} component={Input} validate={[required]} />
            </div>
            <div>
                {/* {createField('password', 'Password', 'password', Input, [required])} */}
                <Field type={'password'} placeholder="Password" name={'password'} component={Input} validate={[required]} />
            </div>
            <div>
                <Field type={'checkbox'} name={'rememberMe'} component={Input} /> remember me
            </div>
            {error &&
                <div className={style.formSummeryError}>
                    {error}
                </div>}
            <div>
                <button>Submit</button>
            </div>
        </form>
    )
}

const LoginReduxForm = reduxForm({
    form: 'Login'
})(LoginForm)
