import Preloader from '../../common/Preloader/Preloader';
import s from './ProfileInfo.module.css'
import ProfileStatusWithHooks from './ProfileStatusHook';
import img from '../../../assets/images/avaDefault.jpeg'

const ProfileInfo = (props) => {
  if (!props.profile) {
    return <Preloader />
  }

  const onNewPhoto = (e) => {
    if (e.target.files.length) {
      props.updatePhoto(e.target.files[0])
    }
  }

  return (
    <div className={s.descriptionBlock}>
      <div className={s.descriptionImg}>
        <div>
          <img src={props.profile.photos.large || img} />
        </div>
        <div>
          {props.isOwner && <input type='file' onChange={onNewPhoto} />}
        </div>
        <div>
          {props.isOwner && <button>edit</button>}
        </div>
      </div>
      {editMode ? DescriptionTextForm : DescriptionText}
      <DescriptionText fullName={props.profile.fullName}
        status={props.status}
        updateStatus={props.updateStatus}
        aboutMe={props.profile.aboutMe}
        lookingForAJob={props.profile.lookingForAJob}
        lookingForAJobDescription ={props.profile.lookingForAJobDescription}
        contacts={props.profile.contacts} />
    </div>
  );
}

const DescriptionTextForm = (props) => {
  return <div>Hi</div>
}

const DescriptionText = (props) => {
  return (
    <div className={s.descriptionText}>
      <div className={s.main}>
        <div className={s.fullName}>{props.fullName}</div>
        <ProfileStatusWithHooks status={props.status} updateStatus={props.updateStatus} />
      </div>
      <div className={s.about}>About Me
        <div>{props.aboutMe}</div>
        <div>About Job: {props.lookingForAJob ? "Looking for a job: " : "Not looking for a job"} {props.lookingForAJobDescription}</div>
        <div>Contacts</div>
        <div className={s.contacts}>
          {Object.keys(props.contacts).map(key => {
            return <div>{key}:{props.contacts[key]}</div>
          })}
        </div>
      </div>
    </div>
  )
}

export default ProfileInfo;
