import { useEffect, useState } from "react"

const News = (props) => {
    const [opacity, setOpacity] = useState({ opacity: 0 })
    const [show, setShow] = useState({show: true})
    useEffect(() => {
        setTimeout(() => fading, 1000)
    }, [])


    const fading = () => {
        const timer = setInterval(() => {
            if (opacity === 1) {
                clearInterval(timer);
            }
            setOpacity({ opacity: opacity + 0.1 })
        }, 100);
    }

    return <div>
        {!show
            ? <div>Regular</div>
            : <div style={{ opacity: opacity }}>Fade In</div>}
    </div>
}

export default News
