import s from './MyPosts.module.css'
import Post from './Post/Post'
import React from 'react';
import { Field, reduxForm } from 'redux-form';
import { maxLengthCreator, required } from '../../../utilits/validators';
import { Textarea } from '../../common/FormsControl/FormsControl';

const maxLength = maxLengthCreator(10)

const MyPosts = React.memo(props => {
  let postsElements = props.postsData
    .map(p => <Post message={p.message} likesCount={p.likesCount} key={p.id} />);

  const onSubmit = (formData) => {
    props.addPost(formData.addPost);
  }

  return (
    <div className={s.postBlock}>
      <h3>My posts</h3>
      <Posts onSubmit={onSubmit}/>
      <div className={s.posts}>
        {postsElements}
      </div>
    </div>
  )
})

const MyPostsForm = (props) => {
  return (
    <form onSubmit={props.handleSubmit}>
      <div className={s.postArea}>
        <Field placeholder="What's new" name={'addPost'} component={Textarea} validate={[required, maxLength]}/>
      </div>
      <div>
        <button>Add post</button>
      </div>
    </form>
  )
}

const Posts = reduxForm({
  form: 'Posts'
})(MyPostsForm)

export default MyPosts;
