import s from './MyPosts.module.css'
import Post from './Post/Post'
import React from 'react'
import { Form, Formik } from 'formik'
import CustomTextarea from '../../common/FormsControl/CustomTextarea'

const MyPostsForm = React.memo(props => {
  let postsElements = props.postsData
    .map(p => <Post message={p.message} likesCount={p.likesCount} key={p.id} />)

  return <div>
    <Formik
      initialValues={{
        addPost: ''
      }}
      onSubmit={(values) => { props.addPost(values) }} //.addPost
    // validationSchema={profileValidation}
    >
      {({ isValid, dirty, isSubmitting }) => (
        <Form className={s.postArea}>
          <div className={s.postBlock}>
            <h3>My posts</h3>
            <div>
              <CustomTextarea placeholder="What's new" name='addPost' type='textarea' />
              <button disabled={!isValid || !dirty || isSubmitting} type={'submit'}>Add post</button>
            </div>
            <div className={s.posts}>
              {postsElements}
            </div>
          </div>
        </Form>
      )}
    </Formik>
  </div>
})

export default MyPostsForm


