        const timer = setInterval(() => {
            if (this.state.opacity === 1) {
                clearInterval(timer);
            }
            this.setState({ opacity: this.state.opacity + 0.1 })
        }, 100);
    }

    componentDidMount() {
        setTimeout(() => this.setState({ show: true }, this.fadingIn), 1000)
    }

    render() {
        return <div>
            {!this.state.show
                ? <div>Regular</div>
                : <div style={{ opacity: this.state.opacity }}>Fade In</div>}
